mapper.select:
SELECT 

FROM book a

WHERE a.id = #{id}

SELECT 
	a.id
FROM book a


	a.del_flag = '0'

		AND a.office_id = #{officeId} 

	AND a.original_id = #{originalId}

SELECT 

FROM book a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.author = #{author}


		AND a.condition = #{condition}


		AND a.tags = #{tags}


		AND a.original_id = #{originalId}


		AND a.publish_date = #{publishDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM book a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE book SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE book SET 
	viewcount = viewcount + 1
WHERE id = #{id}

mapper.select:
SELECT
FROM book a
LEFT JOIN ( SELECT h.book_id, h.create_date as hh_create_date, sum(pay_coin) as totalCoin FROM user_bulkbuychapter_history h group by book_id ) tmp on a.id = tmp.book_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.condition = #{condition}
ORDER BY hh_create_date desc, totalCoin desc

SELECT
FROM book a
LEFT JOIN ( SELECT p.book_id, p.create_date as "p_create_date", sum(p.price) as "price_sum" from payment_bill p where p.create_date >= DATE_SUB(CURDATE(),INTERVAL + 3 DAY) and p.book_id <> '' GROUP BY p.book_id ) tmp on a.id = tmp.book_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.condition = #{condition}
ORDER BY price_sum desc, p_create_date desc

SELECT 

FROM book a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.condition = #{condition}




		ORDER BY ${page.orderBy}


		ORDER BY a.customviewcount DESC

SELECT
FROM book a
LEFT JOIN (SELECT book_id, COUNT(create_by) user_count FROM (SELECT DISTINCT book_id, create_by FROM `user_bulkbuychapter_history` where create_date >= DATE_SUB(CURDATE(),INTERVAL + 3 DAY) ) u GROUP BY book_id ) uu ON a.id = uu.book_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.condition = #{condition}
ORDER BY user_count DESC

SELECT
FROM book a
LEFT JOIN (SELECT book_id, sum(pay_coin) as pay_coin_sum FROM (SELECT book_id, pay_coin FROM `user_buychapter_history` where create_date >= DATE_SUB(CURDATE(),INTERVAL + 3 DAY) ) u GROUP BY book_id ) uu ON a.id = uu.book_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.condition = #{condition}
ORDER BY pay_coin_sum DESC

SELECT 

FROM book a 


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.condition = #{condition}

	AND DATEDIFF(now(), a.create_date)  <  31



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE book SET 	
	customviewcount = #{customviewcount} 
WHERE id = #{id}

mapper.select:
SELECT

FROM book a

LEFT JOIN
(select click_resource, count(1) as "click_count"
from
(select l.click_resource from user_click_log l
where create_time >= DATE_SUB(CURDATE(),INTERVAL + 3 DAY)
) t
GROUP BY click_resource
) tt on a.id = tt.click_resource

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.condition = #{condition}




		ORDER BY ${page.orderBy}


		ORDER BY click_count DESC

SELECT

FROM
book a

LEFT JOIN (
select u.book_id,count(1) AS sort from user_bookshelf u
where u.del_flag = 0
group by u.book_id
) t on t.book_id = a.id

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.condition = #{condition}




		ORDER BY ${page.orderBy}


		ORDER BY t.sort DESC

SELECT
FROM book a
LEFT JOIN ( SELECT click_resource,count(1) AS "click_count" FROM ( SELECT l.click_resource FROM book a INNER JOIN user_click_log l ON l.click_resource = a.id WHERE create_date >= DATE_SUB(CURDATE(), INTERVAL + 30 DAY) AND create_time >= DATE_SUB(CURDATE(), INTERVAL + 30 DAY) ) t GROUP BY click_resource ) tt ON a.id = tt.click_resource
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.condition = #{condition}
ORDER BY ${page.orderBy}
ORDER BY click_count DESC

SELECT

FROM book a

LEFT JOIN
(select click_resource, count(1) as "click_count"
from
(select l.click_resource from user_click_log l
where create_time >= DATE_SUB(CURDATE(),INTERVAL + 7 DAY)
) t
GROUP BY click_resource
) tt on a.id = tt.click_resource

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.condition = #{condition}




		ORDER BY ${page.orderBy}


		ORDER BY click_count DESC

SELECT

FROM book a

LEFT JOIN
(
select book_id from t_fine where is_delete = 0 ORDER BY sort
) t on t.book_id = a.id

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.condition = #{condition}

SELECT
FROM book a
WHERE a.name like #{bookName}

SELECT b.id, b.name, b.title, b.image
FROM `book` b, `user_bulkbuychapter_history` h

	b.id = h.book_id
	AND b.del_flag = #{DEL_FLAG_NORMAL}

		AND b.condition = #{condition}

SELECT
FROM book a
LEFT JOIN ( SELECT book_id FROM search_log GROUP BY book_id ORDER BY SUM(search_count) DESC ) l ON a.id = l.book_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.condition = #{condition}
